#include<stdio.h>
int main ()
{
	float tempCelcius,tempFarenheit;

	printf("Enter Temperature:");
	scanf("%f",&tempCelcius);
	tempFarenheit=((tempCelcius)*(9/5))+32;

	printf("Temperature in Farenheit is: %.2f",tempFarenheit);
	
	if (tempCelcius>=30)
	{printf("\n Its hot! Stay hydrated & wear light clothes");}
	
	else if (tempCelcius>=20 && tempCelcius<=29) 
	{printf("\n Nice weather! perfect for outdoor activities.");}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
	
	else if (tempCelcius>=10 && tempCelcius<=19)
	{printf("\n Cool weather, Wear a light jacket");}
	
	else if (tempCelcius>=0 && tempCelcius<=9)
	{printf("\n Cold ! Wear warm clothes");}
	
	else if (tempCelcius<0)
	{printf("\n Freezing! Stay indoor & bundle up");
	}
	}

